package type;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.nervos.ckb.crypto.Hash;
import org.nervos.ckb.methods.type.Script;
import org.nervos.ckb.service.CKBService;
import org.nervos.ckb.service.HttpService;

import java.io.IOException;
import java.util.Collections;

/** Copyright Â© 2018 Nervos Foundation. All rights reserved. */
@Disabled
public class ScriptTest {

  @Test
  public void testScriptHashWithCodeHash() throws IOException {
    CKBService ckbService = CKBService.build(new HttpService("http://localhost:8114"));
    String codeHash =
        Hash.blake2b(
            "");
    Script script = new Script(codeHash, Collections.emptyList(), Script.DATA);
    Assertions.assertEquals(
        ckbService.computeScriptHash(script).send().getScriptHash(), script.computeHash());
  }
}
